        public static void listen()
        {
            if (KeyListener.keyinfo.Key == ConsoleKey.DownArrow)
            {
                if (map[Play] == Program.SelectChar)
                {
                    SelectExit();
                }
                else if (map[Exit] == Program.SelectChar)
                {
                    SelectPlay();
                }

            }
            if (KeyListener.keyinfo.Key == ConsoleKey.UpArrow)
            {
                if (Menu.map[Play] == Program.SelectChar)
                {
                    SelectExit();
                }
                else if (Menu.map[Exit] == Program.SelectChar)
                {
                    SelectPlay();
                }
            }
            if (KeyListener.keyinfo.Key == ConsoleKey.Enter)
            {
                if (Menu.map[Play] == Program.SelectChar)
                {
                    Program.CurrentMap = Menu2.map;
                    Screen.print();
                }
                else if (Menu.map[Exit] == Program.SelectChar)
                {
                    KeyListener.tjekking = false;
                }
            }
            if (KeyListener.keyinfo.Key == ConsoleKey.Escape)
            {
                KeyListener.tjekking = false;
            }
        }

        private static int Play = 600;
        private static int Exit = 840;
        
        private static void SelectPlay()
        {
            Menu.map[Play] = Program.SelectChar;
            Menu.map[Exit] = ' ';

            Menu.map[Play - 5] = Program.SelectChar2;
            Menu.map[Exit - 5] = ' ';
            Screen.print();
        }
        private static void SelectExit()
        {
            map[Play] = ' ';
            map[Exit] = Program.SelectChar;

            map[Play - 5] = ' ';
            map[Exit - 5] = Program.SelectChar2;
            Screen.print();
        }
